import Slider from 'rc-slider'
import 'rc-slider/assets/index.css'
import { useState, useEffect } from 'react'

import passwordGif from '../../assets/gif/password.gif'
import { ReactComponent as Copy } from '../../assets/icons/copy.svg'
import { ReactComponent as Refresh } from '../../assets/icons/refresh.svg'
import Checkbox from '../Checkbox'
import './index.css'

const PasswordGenerator = () => {
  const [passwordLength, setPasswordLength] = useState<number>(8)
  const [password, setPassword] = useState<string>('')
  const [copyButtonText, setCopyButtonText] = useState('Copy')
  const [includeUppercase, setIncludeUppercase] = useState(true)
  const [includeLowercase, setIncludeLowercase] = useState(false)
  const [includeNumber, setIncludeNumber] = useState(true)
  const [includeSpecialChars, setIncludeSpecialChars] = useState(false)
  const [passwordStrength, setPasswordStrength] = useState<string>('')

  useEffect(() => {
    generatePassword()
  }, [])

  const generatePassword = (): void => {
    const characters: string[] = []
    if (includeUppercase) {
      characters.push(...'ABCDEFGHIJKLMNOPQRSTUVWXYZ')
    }
    if (includeLowercase) {
      characters.push(...'abcdefghijklmnopqrstuvwxyz')
    }
    if (includeNumber) {
      characters.push(...'0123456789')
    }
    if (includeSpecialChars) {
      characters.push(...'!@#$%^&*()')
    }

    let newPassword = ''
    for (let i = 0; i < passwordLength; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length)
      newPassword += characters[randomIndex]
    }
    setPassword(newPassword)
  }

  const copyToClipboard = () => {
    navigator.clipboard
      .writeText(password)
      .then(() => {
        setCopyButtonText('Copied!')
        setTimeout(() => {
          setCopyButtonText('Copy')
        }, 1000)
      })
      .catch((error) => {
        console.error('Failed to copy to clipboard:', error)
      })
  }
  const onChangePasswordLength = (value: number | number[]) => {
    setPasswordLength(value as number)
  }

  return (
    <div className="password-wrapper">
      <div className="gif">
        <img src={passwordGif} alt="Password Gif" />
      </div>
      <div className="tac">
        <h2 className="title">PASSWORD GENERATOR</h2>
        <p className="subtitle">
          Create strong and secure passwords to keep your account safe online.
        </p>
      </div>
      <div className="password-input-wrapper">
        <div className="password-field">
          <input type="text" placeholder="your password" value={password} />
          <Refresh onClick={generatePassword} />
        </div>
        <button className="copy-btn" onClick={copyToClipboard}>
          <Copy /> {copyButtonText}
        </button>
      </div>
      <span className="fw-500">{passwordStrength}</span>
      <div className="slider">
        <div>
          <label id="slider-label">Password Length: </label>
          <span>{passwordLength}</span>
        </div>
        <Slider
          max={30}
          min={5}
          value={passwordLength}
          onChange={onChangePasswordLength}
          className="slider-style"
        />
      </div>
      <div className="elements">
        <Checkbox
          id="uppercase"
          label="Uppercase"
          checked={includeUppercase}
          onChange={() => setIncludeUppercase(!includeUppercase)}
        />
        <Checkbox
          id="lowercase"
          label="Lowercase"
          checked={includeLowercase}
          onChange={() => setIncludeLowercase(!includeLowercase)}
        />
        <Checkbox
          id="numbers"
          label="Numbers"
          checked={includeNumber}
          onChange={() => setIncludeNumber(!includeNumber)}
        />
        <Checkbox
          id="special chars"
          label="Special Characters"
          checked={includeSpecialChars}
          onChange={() => setIncludeSpecialChars(!includeSpecialChars)}
        />
      </div>
    </div>
  )
}

export default PasswordGenerator
